# –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –∫—ç—à–∞
def update_cache(sheet_name):
    print("–û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –∫—ç—à–∞...")
    cache['last_updated'] = time.strftime("%Y-%m-%d %H:%M:%S")
    if sheet_name == "–ö–û–†–ï–Ø":
        cache['korea_data'] = sheet_korea.get_all_records()
        print("–ö—ç—à –æ–±–Ω–æ–≤–ª–µ–Ω korea:", cache['last_updated'])
        return cache["korea_data"]
    elif sheet_name == "–ö–ò–¢–ê–ô":
        cache['china_data'] = sheet_china.get_all_records()
        print("–ö—ç—à –æ–±–Ω–æ–≤–ª–µ–Ω china:", cache['last_updated'])
        return cache["china_data"]
    else:
        return []


# –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–≥–æ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –∫—ç—à–∞ –∫–∞–∂–¥—ã–µ 60 –º–∏–Ω—É—Ç
def start_cache_updater(interval=30):  # 3600 —Å–µ–∫—É–Ω–¥ = 60 –º–∏–Ω—É—Ç
    def updater():
        while True:
            update_cache("–ö–û–†–ï–Ø")
            update_cache("–ö–ò–¢–ê–ô")
            time.sleep(interval)  # –ó–∞–¥–µ—Ä–∂–∫–∞ –≤ 5 –º–∏–Ω—É—Ç

    thread = threading.Thread(target=updater, daemon=True)
    thread.start()





